mapper.select:
SELECT csmt.cust_no ,csmt.cust_nm AS cust_nm_real ,CASE WHEN length(csmt.cust_nm) = 3 THEN substr(csmt.cust_nm, 1, 1) || lpad('*', length(csmt.cust_nm) - 2, '*') || substr(csmt.cust_nm, length(csmt.cust_nm), 1) WHEN length(csmt.cust_nm) > 3 THEN substr(csmt.cust_nm, 1, 2) || lpad('*', length(csmt.cust_nm) - 3, '*') || substr(csmt.cust_nm, length(csmt.cust_nm), 1) ELSE substr(csmt.cust_nm, 1, 1) || lpad('*', length(csmt.cust_nm) - 1, '*') END cust_nm ,REGEXP_REPLACE(REGEXP_REPLACE(csmt.mbl_no, '(.{3})(.*)(.{4})', '\1-\2-\3'), '-(.*)-', '-' ||LPAD('*',LENGTH(REGEXP_REPLACE(csmt.mbl_no, '(.{3})(.*)(.{4})', '\2')), '*') ||'-') mbl_no
,CASE WHEN csmt.cust_ss_cd = '10' THEN cddt.dtl_cd_nm WHEN csmt.cust_ss_cd = '80' THEN cddt.dtl_cd_nm ELSE cddt.dtl_cd_nm END AS cust_ss_cd
,to_char(to_date(csmt.fst_js_dt, 'YYMMDD'), 'YYYY-MM-DD') fst_js_dt ,prtmt.prt_nm ,( SELECT usmt.user_nm FROM ma_user_mt usmt WHERE csmt.lst_upd_id = usmt.user_id ) user_nm ,csmt.lst_upd_id ,to_char(csmt.lst_upd_dt, 'YYYY-MM-DD HH24MISS') AS lst_upd_dt_time FROM cs_cust01_mt csmt LEFT JOIN ma_prt_mt prtmt ON csmt.jn_prt_cd = prtmt.prt_cd LEFT JOIN ma_code_dt cddt on csmt.cust_ss_cd = cddt.dtl_cd LEFT JOIN ma_user_mt usmt ON csmt.lst_upd_id = usmt.user_id where 1 = 1 and cddt.code_cd='CUST_SS_CD' and fst_js_dt between to_date(#{fromDate},'YYYY-MM-DD') and to_date(#{toDate},'YYYY-MM-DD')
and csmt.cust_ss_cd = #{cust_ss_cd}
and cust_no = #{cust_no}
and jn_prt_cd = #{jn_prt_cd}
and cust_no = #{cust_no} and jn_prt_cd = #{jn_prt_cd}
order by cust_no asc
