mapper.select:
SELECT PRTMT.PRT_CD, PRTMT.PRT_NM, to_char(to_date(samt.sal_dt, 'YYMMDD'), 'YYYY-MM-DD') sal_dt, SAMT.cust_no, CSMT.cust_nm, samt.sal_tp_cd, samt.sal_no, case when samt.sal_tp_cd = 'RTN' then samt.tot_sal_qty *(-1) else samt.tot_sal_qty end tot_sal_qty, case when samt.sal_tp_cd = 'RTN' then samt.tot_sal_amt *(-1) else samt.tot_sal_amt end tot_sal_amt, case when samt.sal_tp_cd = 'RTN' then samt.csh_stlm_amt *(-1) else samt.csh_stlm_amt end csh_stlm_amt, case when samt.sal_tp_cd = 'RTN' then samt.crd_stlm_amt *(-1) else samt.crd_stlm_amt end crd_stlm_amt, case when samt.sal_tp_cd = 'RTN' then samt.pnt_stlm_amt *(-1) else samt.pnt_stlm_amt end pnt_stlm_amt,
samt.lst_upd_id, ( SELECT usmt.user_nm FROM ma_user_mt usmt WHERE samt.lst_upd_id = usmt.user_id ) user_nm, to_char(samt.lst_upd_dt, 'YYYY-MM-DD') AS lst_upd_dt_time FROM cs_sal01_mt samt LEFT JOIN CS_CUST01_MT CSMT ON csmt.cust_no = samt.cust_no LEFT JOIN MA_PRT_MT PRTMT ON samt.prt_cd = PRTMT.prt_cd LEFT JOIN ma_user_mt usmt ON samt.lst_upd_id = usmt.user_id
WHERE 1 = 1
and SAL_DT between to_date(#{fromDate},'YYYY-MM-DD') and to_date(#{toDate},'YYYY-MM-DD') and prtmt.prt_cd = #{jn_prt_cd}
and samt.cust_no = #{cust_no}
order by SAL_DT DESC,SAMT.SAL_NO ASC

insert.foreach:
INTO CS_SAL01_MT ( PRT_CD, SAL_DT, SAL_NO, SAL_TP_CD, TOT_SAL_QTY, TOT_SAL_AMT, TOT_VOS_AMT, TOT_VAT_AMT, CSH_STLM_AMT, CRD_STLM_AMT, PNT_STLM_AMT, CUST_NO, CRD_NO, VLD_YM, CRD_CO_CD, FST_REG_DT, FST_USER_ID, LST_UPD_DT, LST_UPD_ID ) VALUES ( #{list.prt_cd}, to_char(SYSDATE, 'YYYYMMDD'), ( select nvl(max(sal_no),0) + 1 FROM CS_SAL01_MT WHERE PRT_CD = #{list.prt_cd} AND SAL_DT = #{list.sal_dt} AND SAL_NO = #{list.sal_no}
),
'RTN', #{list.tot_sal_qty}, #{list.tot_sal_amt}, #{list.tot_vos_amt}, #{list.tot_vat_amt}, #{list.csh_stlm_amt}, #{list.crd_stlm_amt}, #{list.pnt_stlm_amt}, #{list.cust_no}, #{list.crd_no, jdbcType=VARCHAR}, #{list.vld_ym, jdbcType=VARCHAR}, #{list.crd_co_cd, jdbcType=VARCHAR}, #{list.fst_reg_dt}, #{list.fst_user_id}, SYSDATE, #{list.user_id} )
